// Function to generate schema from attribute definitions
const generateSpeciesSchema = (baseSchema, attributeDefinitions) => {
  if (!attributeDefinitions || !attributeDefinitions.length) return baseSchema;

  const dynamicProperties = {};

  // Track which level1 and level2 categories have been added
  let addedLevel1Categories = new Set();
  let addedLevel2Categories = new Set();

  attributeDefinitions.forEach(attr => {
    const placeholder = attr.description || " ";

    // Add level1 category as a heading if it hasn't been added yet
    if (!addedLevel1Categories.has(attr.level1_category)) {
      dynamicProperties[`level1_${attr.level1_category}`] = {
        type: "text",
        label: "",
        value: attr.level1_category_display_name,
        styles: {
          fontSize: 20,
          fontWeight: 400
        }
      };
      addedLevel1Categories.add(attr.level1_category);
    }

    // Add level2 category as a heading if it hasn't been added yet and is different from level1
    if (attr.level2_category && 
        !addedLevel2Categories.has(attr.level2_category)) {
      dynamicProperties[`level2_${attr.level2_category}`] = {
        type: "text",
        label: "",
        value: attr.level2_category_display_name,
        styles: {
          fontSize: 16,
          fontWeight: 600
        }
      };
      addedLevel2Categories.add(attr.level2_category);
    }

    // Add dynamic fields based on attribute definitions
    // note it uses the ID as it's the foreign key required for attribute creation
    //dynamicProperties[attr.field_name] = {
    dynamicProperties[`attr_${attr.id}`] = {
      type: attr.input_type || "textarea",
      label: attr.display_name,
      value: speciesAttributes[attr.id]?.value,
      placeholder: placeholder,
    };

    // add explanation as text if applicable
    if (attr.explanation) {
      dynamicProperties[`expl_${attr.id}`] = {
        type: "text",
        value: " ",
        label: attr.explanation,
        styles: {
          fontSize: 12,
          textColor: 'gray'
        }
      };
    }

    // Add sources field underneath if applicable
    if (attr.has_sources) {
      dynamicProperties[`src_${attr.id}`] = {
        type: "textinput",
        label: "Quellenangabe",
        value: speciesAttributes[attr.id]?.sources,
        placeholder: " ",
      };
    } 

    // Add required field if applicable
    if (attr.required) {
      baseSchema.required.push(attr.field_name);
    }

    // add gap for layout purposes when sources required
    if (attr.has_sources) {
      dynamicProperties[`gap_${attr.id}`] = {
        type: "text",
        value: " ",
        label: " ",
        styles: {
          fontSize: 4,
          textColor: 'gray'
        }
      };
    }
  });

  baseSchema.properties = { ...baseSchema.properties, ...dynamicProperties };
  return baseSchema;
};

