  if (speciesAttributeIds[attributeValue.attributeDefinitionId] == undefined) {
    return queries.CreateSpeciesAttribute.run({
      ...attributeValue,
      sources: srcMap[attributeValue.attributeDefinitionId]
    });
  }
  // attribute already exists --> update
  return queries.UpdateSpeciesAttributeValue.run(attributeValue);
});

